import phone from "../img/phone.PNG";
import resto from "../img/resto.PNG";

const Row = (props) => {
  return (
    <div className="row">
      <div className="col-1">
        <div className="pre-head">{props.preHead}</div>
        <h1 className="head">{props.head}</h1>
        <div className="desc">
          <p>{props.desc1}</p>
          <p>{props.desc2}</p>
          <p>{props.desc3}</p>
        </div>
      </div>
      <div className="col-2">
        <img src={props.img} alt="Phone with food" />
      </div>
    </div>
  );
};

const Rows = () => {
  return (
    <>
      <Row
        preHead={"SELL FOOD ONLINE"}
        head={"Turn people visiting your website into regular food clients"}
        desc1={
          "Our online ordering food System will help you transform your website into a money-making machine"
        }
        desc2={
          "No matter how much your busines grows, you will always be able to take free unlimited orders with zero costs."
        }
        desc3={
          "Power your business with our free restaurant online ordering system & you'll never have to worry about fees or commissions."
        }
        img={phone}
      />
      <Row
        preHead={"RECEIVE MORE ORDERS"}
        head={
          "Get more clients to buy from you. Use easy-to-spot restaurant widgets."
        }
        desc1={
          "The harder it is for your hungry customers to find the ordering buttons on your website, the more customers you will lose."
        }
        desc2={'Increase customer retention, by adding the "See MENU & Order" *'}
        img={resto}
      />
      <Row />
      <Row />
      <Row />
    </>
  );
};

export default Rows;
